// Function definition
function checkEvenOrOdd(num) {
  if (num % 2 === 0) {
    return 'Even';
  } else {
    return 'Odd';
  }
}

// Example usage
console.log(checkEvenOrOdd(7)); // Output: Odd

// Function definition
function factorial(num) {
  if (num === 0 || num === 1)
    return 1;
  for (var i = num - 1; i >= 1; i--) {
    num *= i;
  }
  return num;
}

// Example usage
console.log(factorial(5)); // Output: 120

// Function definition
