num2 <- 60
result <- gcd(num1, num2)
print(paste('GCD of', num1, 'and', num2, 'is', result))

# Function to check Armstrong number
is_armstrong <- function(num) {
  digits <- nchar(num)
  sum <- sum(as.numeric(strsplit(as.character(num), '')[[1]])^digits)
  return(sum == num)
}

# Finding Armstrong numbers between two numbers
find_armstrong_numbers <- function(start, end) {
  armstrong_numbers <- c()
  for (num in start:end) {
    if (is_armstrong(num))
      armstrong_numbers <- c(armstrong_numbers, num)
  }
  return(armstrong_numbers)
}

# Example usage
start_num <- 100
end_num <- 1000
armstrong_nums <- find_armstrong_numbers(start_num, end_num)
print(paste('Armstrong numbers between', start_num, 'and', end_num, 'are:', paste(armstrong_nums, collapse=', ')))

# Function to find sum of digits of a number
sum_of_digits <- function(num) {
  sum <- 0
  while (num > 0) {
    sum <- sum + (num %% 10)
    num <- num %/% 10
  }
  return(sum)
}

# Example usage
number <- 12345
sum <- sum_of_digits(number)
print(paste('Sum of digits of', number, 'is', sum))

# Function to check perfect number
is_perfect <- function(num) {
  sum_divisors <- sum(which(num %% 1:num == 0)) - num
  return(sum_divisors == num)
}

# Example usage
number <- 28
if (is_perfect(number))
  print(paste(number, 'is a perfect number.'))
else
  print(paste(number, 'is not a perfect number.'))

