fun sumOfDigits(num: Int): Int {
    var n = num
    var sum = 0
    while (n != 0) {
        sum += n % 10
        n /= 10
    }
    return sum
}

fun main() {
    val num = 12345
    println("Sum of digits of $num is: ${sumOfDigits(num)}")
}

fun reverseNumber(num: Int): Int {
    var n = num
    var reversed = 0
    while (n != 0) {
        val digit = n % 10
        reversed = reversed * 10 + digit
        n /= 10
    }
    return reversed
}

fun main() {
    val num = 12345
    println("Reversed number of $num is: ${reverseNumber(num)}")
}

fun gcd(a: Int, b: Int): Int {
    var x = a
    var y = b
    while (y != 0) {
        val temp = y
        y = x % y
        x = temp
    }
    return x
}

fun lcm(a: Int, b: Int): Int {
    return a * b / gcd(a, b)
}

fun main() {
    val num1 = 24
    val num2 = 36
    println("LCM of $num1 and $num2 is: ${lcm(num1, num2)}")
}

fun isPerfectNumber(num: Int): Boolean {
    var sum = 0
    for (i in 1 until num) {
        if (num % i == 0)
            sum += i
    }
    return sum == num
}

fun main() {
    val num = 28
    if (isPerfectNumber(num))
        println("$num is a perfect number")
    else
        println("$num is not a perfect number")
}

