// SCSS syntax
@function contrast-color($background-color) {
  // Convert hex to RGB values
  $red: red($background-color);
  $green: green($background-color);
  $blue: blue($background-color);
  // Calculate luminance
  $luminance: 0.2126 * $red + 0.7152 * $green + 0.0722 * $blue;
  // Return contrasting color based on luminance
  @if ($luminance > 128) {
    @return #000000; // Light background, return black
  } @else {
    @return #ffffff; // Dark background, return white
  }
}

