  $hex-green: str-slice(#{(100 + $green) * 255 / 256}, 2);
  $hex-blue: str-slice(#{(100 + $blue) * 255 / 256}, 2);
  @return ##{$hex-red}#{$hex-green}#{$hex-blue};
}

