func isPrime(num int) bool {
    if num <= 1 {
        return false
    }
    limit := int(math.Sqrt(float64(num)))
    for i := 2; i <= limit; i++ {
        if num%i == 0 {
            return false
        }
    }
    return true
}

func main() {
    var num int
    fmt.Print("Enter a number: ")
    fmt.Scanln(&num)
    if isPrime(num) {
        fmt.Println(num, "is a prime number")
    } else {
        fmt.Println(num, "is not a prime number")
    }
}

