import (
    "fmt"
    "math/big"
)

func factorial(n int) *big.Int {
    if n < 0 {
        return big.NewInt(1)
    }
    if n == 0 {
        return big.NewInt(1)
    }
    bigN := big.NewInt(int64(n))
    return bigN.Mul(bigN, factorial(n-1))
}

func main() {
    var num int
    fmt.Print("Enter a number: ")
    fmt.Scanln(&num)
    fmt.Println("Factorial:", factorial(num))
}

