    "math/big"
)

func fibonacci(n int) []*big.Int {
    fibs := []*big.Int{big.NewInt(0), big.NewInt(1)}
    for i := 2; i < n; i++ {
        fib := big.NewInt(0)
        fib.Add(fibs[i-1], fibs[i-2])
        fibs = append(fibs, fib)
    }
    return fibs
}

func main() {
    var num int
    fmt.Print("Enter the number of terms: ")
    fmt.Scanln(&num)
    fmt.Println("Fibonacci Series up to", num, "terms:", fibonacci(num))
}

