import (
    "fmt"
    "math"
)

func isArmstrong(num int) bool {
    var sum, original, remainder int
    original = num
    for {
        remainder = num % 10
        sum += int(math.Pow(float64(remainder), float64(3)))
        num /= 10
        if num == 0 {
            break
        }
    }
    return sum == original
}

func main() {
    var num int
    fmt.Print("Enter a number: ")
    fmt.Scanln(&num)
    if isArmstrong(num) {
        fmt.Println(num, "is an Armstrong number")
    } else {
        fmt.Println(num, "is not an Armstrong number")
    }
}

