func gcd(a, b int64) *big.Int {
    bigA := big.NewInt(a)
    bigB := big.NewInt(b)
    return bigA.GCD(nil, nil, bigB)
}

func lcm(a, b int64) *big.Int {
    gcd := gcd(a, b)
    bigA := big.NewInt(a)
    bigB := big.NewInt(b)
    return bigA.Mul(bigA.Div(bigA, gcd), bigB)
}

func main() {
    var num1, num2 int64
    fmt.Print("Enter two numbers: ")
    fmt.Scanln(&num1, &num2)
    fmt.Println("LCM:", lcm(num1, num2))
}

