import (
    "fmt"
)

func generatePascalsTriangle(numRows int) [][]int {
    triangle := make([][]int, numRows)
    for i := range triangle {
        triangle[i] = make([]int, i+1)
        triangle[i][0], triangle[i][i] = 1, 1
        for j := 1; j < i; j++ {
            triangle[i][j] = triangle[i-1][j-1] + triangle[i-1][j]
        }
    }
    return triangle
}

func main() {
    var numRows int
    fmt.Print("Enter the number of rows for Pascal's triangle: ")
    fmt.Scanln(&numRows)
    triangle := generatePascalsTriangle(numRows)
    fmt.Println("Pascal's Triangle upto", numRows, "rows:")
    for _, row := range triangle {
        fmt.Println(row)
    }
}

