from itertools import combinations

# Assuming transactions is loaded from data
transactions = [['milk', 'bread', 'butter'], ['bread', 'butter'], ['milk', 'bread']]
min_support = 2

def apriori(transactions, min_support):
    itemsets = []
    for transaction in transactions:
        for item in transaction:
            if not {item} in itemsets:
                itemsets.append({item})
    print('Frequent Itemsets:', itemsets)

apriori(transactions, min_support)

